// GENERAL ///////////////////////////////////////

@mixin flex-center {
  display: flex;
  justify-content: center;
  align-items: center;
}

@mixin center {
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
}

@mixin center-vertical {
  position: absolute;
  top: 50%;
  transform: translateY(-50%);
}
@mixin center-horizontal {
  position: absolute;
  left: 50%;
  transform: translateX(-50%);
}

@mixin image-correct {
  height: 100%;
  width: 100%;
  object-fit: cover;
}

@mixin clearfix {
  &::after {
    content: "";
    display: table;
    clear: both;
  }
}

// ARROW BACKGROUND ////////////////////////////

@mixin background-transparent($side) {
  @include center-vertical();
  #{$side}: 8%;
  height: 8rem;
  width: 8rem;
  background-color: $color-white_transparent;
  border-radius: 7rem;
}

// SLIDE TEXTBOX  ////////////////////////////

@mixin textbox-composition($side, $percentage) {
  #{$side}: #{$percentage};
  position: absolute;
  background-color: $color-primary_dark-transparent;
  border-radius: 2rem;
  box-shadow: 0 0 5rem 5rem $color-primary_dark-transparent;
  z-index: 9999;
}

sources.src\\sass\\_button.scss:
.button {
  &,
  &:link,
  &:visited {
    padding: 1rem 3rem;
    border: none;
    background-color: $color-primary;
    color: $color-white;
    font-size: 1.4rem;
    font-weight: 600;
    transition: all 300ms;
  }

  &:not(:last-child) {
    margin-right: 3rem;
  }

  // HEADER BUTTONS////////////////////////////////////

  &--header {
    &:hover {
      background-color: $color-primary_dark;
    }

    &:active,
    &:focus {
      transition: all 100ms;
      transform: translateY(0.2rem);
    }
  }

  &--fb {
    transition: all 200ms;

    &:hover {
      transform: scale(1.1);
    }

    &:active,
    &:focus {
      transform: none;
    }
  }

  // NAVIGATION BUTTONS////////////////////////////////

  &--navigation {
    &:hover {
      border-bottom: 0.5rem solid $color-white;
    }

    &:active,
    &:focus {
      transition: all 50ms;
      border-bottom: 0.1rem solid $color-white;
    }

    background-color: $color-primary_dark;
  }

  // INTRO BUTTON//////////////////////////////////////

  &--intro {
    position: absolute;
    font-size: 1.2rem;
    font-weight: normal;
    left: 15%;
    padding-right: 1rem;

    &::after {
      content: '';
      clip-path: polygon(0 100%, 0 0, 60% 50%);
      height: 1.5rem;
      width: 1.5rem;
      background-color: $color-white;
      margin-left: 2rem;
    }

    &:hover {
      background-color: $color-primary_dark;
    }

    &:active,
    &:focus {
      transition: all 100ms;
      transform: translateX(0.3rem);
    }
  }

  // OVERVIEW //////////////////////////////////////

  &--overview {
    height: 10rem;
    width: 5rem;
    font-size: 5rem;
  }

  &--svg {
    width: 5rem;
    filter: $svg-white;
  }
}

