.lazy-background {
  background-image: linear-gradient(
      rgb(255, 255, 255),
      rgba(255, 255, 255, 0.76)
    ),
    url('../img/background1-lazy.jpg');
  filter: blur(4px);
}

.lazy-background.visible {
  background-image: linear-gradient(
      rgb(255, 255, 255),
      rgba(255, 255, 255, 0.76)
    ),
    url('../img/background1.jpg');
  filter: none;
}

.intro {
  width: 100%;
  height: 55rem;
  background-size: cover;

  &--video {
    position: relative;
    width: 50%;
    height: 100%;

    &__container {
      position: relative;
      top: 50%;
      transform: translateY(-55%);
      padding-bottom: 56.25%;
      padding-top: 2.5rem;
      width: 65rem;
      margin-left: auto;
    }

    &__content {
      position: absolute;
      width: 100%;
      height: 100%;
    }
  }

  &--textbox {
    position: relative;
    width: 50%;
    height: max-content;
    text-align: center;
    transform: translateY(-1.6rem);

    &__heading {
      width: 28rem;
      transform: translateX(-11rem) translateY(2.5rem);

      &-sub {
        vertical-align: middle;
        color: $color-primary_dark;
        font-size: 4rem;
        transform: translateX(5rem);
        margin-bottom: 2rem;
      }
    }

    &__text {
      margin: 0 auto 1rem auto;
      width: 70%;
      text-align: start;

      &-sub {
        margin: 0 auto 2.4rem auto;
        width: 70%;
        text-align: start;
        font-weight: $fw-bold;
      }
    }
  }
}

