.button {
  &,
  &:link,
  &:visited {
    padding: 1rem 3rem;
    border: none;
    background-color: $color-primary;
    color: $color-white;
    font-size: 1.4rem;
    font-weight: 600;
    transition: all 300ms;
    box-shadow: $box-shadow;
    cursor: pointer;
  }

  &:not(:last-child) {
    margin-right: 3rem;
  }

  // HEADER BUTTONS////////////////////////////////////

  &--normal {
    &:hover {
      background-color: $color-primary_dark;
    }

    &:active,
    &:focus {
      transition: all 100ms;
      transform: translateY(0.2rem);
    }

    & p {
      color: $color-white;
    }
  }

  &--fb {
    transition: all 200ms;
    box-shadow: $box-shadow;

    &:hover {
      transform: scale(1.1);
    }

    &:active,
    &:focus {
      transform: none;
    }
  }

  // NAVIGATION BUTTONS////////////////////////////////

  &--navigation {
    &:hover {
      border-bottom: 0.5rem solid $color-white;
      box-shadow: $box-shadow;
    }

    &:active,
    &:focus {
      transition: all 50ms;
      border-bottom: 0.1rem solid $color-white;
    }

    box-shadow: none;
    background-color: $color-primary_dark;
  }

  // INTRO BUTTON//////////////////////////////////////

  &--intro {
    position: absolute;
    font-size: 1.5rem;
    font-weight: $fw-med;
    left: 15%;
    padding-right: 1rem;

    &::after {
      content: '';
      clip-path: polygon(0 100%, 0 0, 60% 50%);
      height: 1.5rem;
      width: 1.5rem;
      background-color: $color-white;
      margin-left: 2rem;
    }

    &:hover {
      background-color: $color-primary_dark;
    }

    &:active,
    &:focus {
      transition: all 100ms;
      transform: translateX(0.6rem);
    }
  }

  // OVERVIEW //////////////////////////////////////

  &--overview {
    height: 10rem;
    width: 5rem;
    font-size: 5rem;
  }

  &--svg {
    width: 5rem;
    filter: $svg-white;
  }

  // INQUIRY FORM /////////////////////////////////

  &--form {
    background-color: $color-primary_dark-transparent--med;
    & p {
      color: $color-white;
    }

    &:hover {
      background-color: $color-primary_dark;
    }

    &:active,
    &:focus {
      transition: all 100ms;
      transform: translateX(0.6rem);
    }
  }

  &--form__svg {
    width: 4rem;
    filter: $svg-white;
    margin-left: 1.5rem;
  }
}

