.carousel {
  &--container {
    width: 100%;
  }

  height: 60rem;
  position: relative;
  overflow: hidden;

  &--image {
    @include image-correct;
  }

  &--slider {
    height: 100%;
    display: flex;
    width: 400%;
    transition: all 350ms;

    & section {
      @include flex-center;
      flex-basis: 100%;
      position: relative;
    }
  }

  &--textbox {
    &__png {
      height: 15rem;
      width: auto;
    }

    &__left {
      @include textbox-composition(left, 17%);

      & h1 {
        margin: -3rem 0 0 15rem;
        padding: 0;
      }

      & h2 {
        margin: 0.4rem 0 0 15rem;
      }
    }

    &__right {
      @include textbox-composition(right, 17%);

      & h1 {
        margin-top: -1.7rem;
        padding: 0;
      }

      & h2 {
        margin-top: 0.4rem;
      }
    }
    &__center {
      @include textbox-composition(left, 37%);
      top: 14%;

      & h1 {
        margin: -3rem 0 0 7rem;
        padding: 0;
      }

      & h2 {
        margin: 0.4rem 0 0 7rem;
      }
    }
  }
}

.arrow {
  @include center-vertical;
  width: 5rem;
  height: 5rem;

  &s {
    cursor: pointer;
  }

  &--left {
    left: 8%;
    background-color: $color-primary_dark;
    clip-path: polygon(100% 100%, 100% 0, 40% 50%);

    &__background {
      @include background-transparent(left);
    }
  }

  &--right {
    right: 8%;
    background-color: $color-primary_dark;
    clip-path: polygon(0 100%, 0 0, 60% 50%);

    &__background {
      @include background-transparent(right);
    }
  }
}

.ul {
  @include center-horizontal;
  bottom: 2rem;
  list-style-type: none;
  display: flex;
  padding: 0;
  margin: 0;
  background-color: $color-white_transparent;
  border-radius: 5rem;

  &--li {
    width: 2.2rem;
    height: 2.2rem;
    border: 0.3rem solid #236097;
    border-radius: 5rem;
    margin: 1rem 1.2rem;
    background: $color-primary_dark;
    cursor: pointer;
  }

  & .selected {
    background: $color-white;
  }
}

