generated.css:
*,
*::before,
*::after {
  box-sizing: border-box;
  margin: 0;
  padding: 0;
}

/* General styles */
html {
  font-size: 45%;
  font-family: "Source Sans Pro", sans-serif;
  font-weight: 400;
  scroll-behavior: smooth;
}

body {
  background: #e7e7e7;
  margin: 0;
  width: 152rem;
  margin: 0 auto;
}

p {
  font-size: 1.5rem;
  color: #236097;
}

h1,
h2,
h3 {
  color: #e7e7e7;
  line-height: 4rem;
}

h1 {
  font-size: 5rem;
  font-weight: 600;
}

h2 {
  font-size: 3.5rem;
}

a {
  cursor: pointer;
  text-decoration: none;
}

.flex {
  display: flex;
  justify-content: center;
  align-items: center;
}

.button, .button:link, .button:visited {
  padding: 1rem 3rem;
  border: none;
  background-color: #0080ff;
  color: #fff;
  font-size: 1.4rem;
  font-weight: 600;
  transition: all 300ms;
  box-shadow: 0.25em 0.25em 0.75em rgba(0, 0, 0, 0.25), 0.125em 0.125em 0.25em rgba(0, 0, 0, 0.15);
  cursor: pointer;
}
.button:not(:last-child) {
  margin-right: 3rem;
}
.button--normal:hover {
  background-color: #236097;
}
.button--normal:active, .button--normal:focus {
  transition: all 100ms;
  transform: translateY(0.2rem);
}
.button--normal p {
  color: #fff;
}
.button--fb {
  transition: all 200ms;
  box-shadow: 0.25em 0.25em 0.75em rgba(0, 0, 0, 0.25), 0.125em 0.125em 0.25em rgba(0, 0, 0, 0.15);
}
.button--fb:hover {
  transform: scale(1.1);
}
.button--fb:active, .button--fb:focus {
  transform: none;
}
.button--navigation {
  box-shadow: none;
  background-color: #236097;
}
.button--navigation:hover {
  border-bottom: 0.5rem solid #fff;
  box-shadow: 0.25em 0.25em 0.75em rgba(0, 0, 0, 0.25), 0.125em 0.125em 0.25em rgba(0, 0, 0, 0.15);
}
.button--navigation:active, .button--navigation:focus {
  transition: all 50ms;
  border-bottom: 0.1rem solid #fff;
}
.button--intro {
  position: absolute;
  font-size: 1.5rem;
  font-weight: 600;
  left: 15%;
  padding-right: 1rem;
}
.button--intro::after {
  content: "";
  clip-path: polygon(0 100%, 0 0, 60% 50%);
  height: 1.5rem;
  width: 1.5rem;
  background-color: #fff;
  margin-left: 2rem;
}
.button--intro:hover {
  background-color: #236097;
}
.button--intro:active, .button--intro:focus {
  transition: all 100ms;
  transform: translateX(0.6rem);
}
.button--overview {
  height: 10rem;
  width: 5rem;
  font-size: 5rem;
}
.button--svg {
  padding-top: 0.3rem;
  width: 4.5rem;
  filter: invert(100%) sepia(91%) saturate(38%) hue-rotate(254deg) brightness(110%) contrast(110%);
}
.button--form {
  background-color: #236197cc;
}
.button--form p {
  color: #fff;
}
.button--form:hover {
  background-color: #236097;
}
.button--form:active, .button--form:focus {
  transition: all 100ms;
  transform: translateX(0.6rem);
}
.button--form__svg {
  width: 4rem;
  filter: invert(100%) sepia(91%) saturate(38%) hue-rotate(254deg) brightness(110%) contrast(110%);
  margin-left: 1.5rem;
}

.form {
  height: 72rem;
}
.form__group:not(:last-child) {
  margin-bottom: 2rem;
}
.form__input {
  font-size: 2rem;
  font-weight: 600;
  font-family: inherit;
  color: #236097;
  padding: 1.5rem 2rem;
  border-radius: 2px;
  background-color: #fff;
  border: none;
  border-bottom: 3px solid transparent;
  width: 87.7%;
  display: block;
  transition: all 0.3s;
}
.form__input__message {
  height: 20rem;
  padding: 1.5rem 2rem 15rem 2rem;
  overflow: hidden;
  resize: none;
}
.form__input:focus {
  outline: none;
  color: #0080ff;
  box-shadow: 0 1rem 2rem rgba(0, 0, 0, 0.1);
  border-bottom: 3px solid lightgreen;
}
.form__input:focus:invalid {
  border-bottom: 3px solid red;
}
.form__input::-webkit-input-placeholder {
  color: #236097;
}
.form__label {
  font-size: 1.5rem;
  font-weight: 700;
  margin-left: 2rem;
  margin-top: 0.7rem;
  color: #236097;
  display: block;
  transition: all 0.3s;
}
.form__input:placeholder-shown + .form__label {
  opacity: 0;
  visibility: hidden;
  transform: translateY(-4rem);
}

/**************************/
/* HEADER */
/**************************/
.header {
  height: 10rem;
