}
.carousel--textbox__right {
  right: 17%;
  position: absolute;
  background-color: #2361977e;
  border-radius: 2rem;
  box-shadow: 0 0 5rem 5rem #2361977e;
  z-index: 9999;
}
.carousel--textbox__right h1 {
  margin-top: -1.7rem;
  padding: 0;
}
.carousel--textbox__right h2 {
  margin-top: 0.4rem;
}
.carousel--textbox__center {
  left: 37%;
  position: absolute;
  background-color: #2361977e;
  border-radius: 2rem;
  box-shadow: 0 0 5rem 5rem #2361977e;
  z-index: 9999;
  top: 14%;
}
.carousel--textbox__center h1 {
  margin: -3rem 0 0 7rem;
  padding: 0;
}
.carousel--textbox__center h2 {
  margin: 0.4rem 0 0 7rem;
}

.arrow {
  position: absolute;
  top: 50%;
  transform: translateY(-50%);
  width: 5rem;
  height: 5rem;
}
.arrows {
  cursor: pointer;
}
.arrow--left {
  left: 8%;
  background-color: #236097;
  clip-path: polygon(100% 100%, 100% 0, 40% 50%);
}
.arrow--left__background {
  position: absolute;
  top: 50%;
  transform: translateY(-50%);
  left: 8%;
  height: 8rem;
  width: 8rem;
  background-color: #ffffff5d;
  border-radius: 7rem;
}
.arrow--right {
  right: 8%;
  background-color: #236097;
  clip-path: polygon(0 100%, 0 0, 60% 50%);
}
.arrow--right__background {
  position: absolute;
  top: 50%;
  transform: translateY(-50%);
  right: 8%;
  height: 8rem;
  width: 8rem;
  background-color: #ffffff5d;
  border-radius: 7rem;
}

.ul {
  position: absolute;
  left: 50%;
  transform: translateX(-50%);
  bottom: 2rem;
  list-style-type: none;
  display: flex;
  padding: 0;
  margin: 0;
  background-color: #ffffff5d;
  border-radius: 5rem;
}
.ul--li {
  width: 2.2rem;
  height: 2.2rem;
  border: 0.3rem solid #236097;
  border-radius: 5rem;
  margin: 1rem 1.2rem;
  background: #236097;
  cursor: pointer;
}
.ul .selected {
  background: #fff;
}

.lazy-background {
  background-image: linear-gradient(white, rgba(255, 255, 255, 0.76)), url("/f110540e1f55f1685092093d90fcc483.jpg");
  filter: blur(4px);
}

.lazy-background.visible {
  background-image: linear-gradient(white, rgba(255, 255, 255, 0.76)), url("/24f10a29690367a87c5ebca27fd97b6b.jpg");
  filter: none;
}

.intro {
  width: 100%;
  height: 55rem;
  background-size: cover;
}
.intro--video {
  position: relative;
  width: 50%;
  height: 100%;
}
.intro--video__container {
  position: relative;
  top: 50%;
  transform: translateY(-50%);
  padding-bottom: 48.25%;
  width: 65rem;
  margin-left: auto;
  box-shadow: 0.25em 0.25em 0.75em rgba(0, 0, 0, 0.25), 0.125em 0.125em 0.25em rgba(0, 0, 0, 0.15);
}
.intro--video__content {
  position: absolute;
  width: 100%;
  height: 100%;
}
.intro--textbox {
  position: relative;
  width: 50%;
  height: max-content;
  text-align: center;
  transform: translateY(-1.6rem);
}
.intro--textbox__heading {
  width: 28rem;
  transform: translateX(-11rem) translateY(2.5rem);
}
.intro--textbox__heading-sub {
  vertical-align: middle;
  color: #236097;
  font-size: 4rem;
  transform: translateX(5rem);
  margin-bottom: 2rem;
}
.intro--textbox__text {
  margin: 0 auto 1rem auto;
  width: 70%;
  text-align: start;
}
.intro--textbox__text-sub {
  margin: 0 auto 2.4rem auto;
  width: 70%;
  text-align: start;
  font-weight: 900;
}

/* OPERATIONS */
.overview {
  position: relative;
  max-width: 119rem;
  margin-top: 9rem;
  height: 50rem;
  background-color: #236097;
  background-image: url("/fb030b9acabc46c8764f7af3a8df6567.png");
  background-size: cover;
}
.overview::before {
  content: "";
  position: absolute;
  background-color: inherit;
  height: inherit;
  bottom: 0;
  width: 50vw;
  left: -50vw;
}
.overview--header {
  padding: 4rem 0 0 11rem;
  position: relative;
  font-weight: 600;
  width: max-content;
}
.overview--header::after {
  content: "";
  position: absolute;
  left: 50%;
  transform: translateX(-50%);
  bottom: -1.5rem;
  left: 60%;
  width: 45rem;
  height: 0.5rem;
  background-color: #fff;
}
.overview--header strong {
  font-weight: 900;
}
.overview--header__sub {
  font-size: 3.5rem;
  font-weight: 500;
  align-self: start;
  margin-bottom: 2rem;
}
.overview--content p {
  color: inherit;
}
.overview--content p:not(:last-child) {
  margin-bottom: 1.5rem;
}
.overview--tab {
  position: relative;
  height: 9rem;
  width: 9rem;
  border-radius: 5rem;
  margin-top: 5rem;
  border: 0.4rem solid white;
  background-color: #236097;
}
.overview--tab__container {
  display: flex;
  justify-content: start;
  margin-left: 12rem;
  background-color: transparent;
  height: 11rem;
  margin-bottom: 2rem;
}
.overview--tab:hover {
  background-color: #0080ff;
}
.overview--tab:not(:last-child) {
  margin-right: 8rem;
}
.overview--tab__active::after {
  content: "";
  position: absolute;
  bottom: -2rem;
  left: 0;
  right: 0;
  margin: 0 auto;
  width: 0.1rem;
  border-left: 1.2rem solid transparent;
  border-right: 1.2rem solid transparent;
  border-top: 1.8rem solid #fff;
}
.overview--content {
  transition: all 300ms;
  display: none;
  font-size: 1.7rem;
  padding: 7rem 0rem 6.5rem 11rem;
  width: 90rem;
  color: #fff;
}
.overview--content__image {
  position: absolute;
  right: -20rem;
  top: 5rem;
  width: 40rem;
  height: 40rem;
  object-fit: cover;
  box-shadow: 0.25em 0.25em 0.75em rgba(0, 0, 0, 0.25), 0.125em 0.125em 0.25em rgba(0, 0, 0, 0.15);
}
.overview--content__active {
  display: flex;
}

.services {
  height: 75rem;
}
.services--intro {
  margin-top: 9rem;
  margin-left: 11rem;
  width: 105rem;
}
.services--intro__heading {
  position: relative;
  width: max-content;
  font-size: 4.5rem;
  font-weight: 600;
  color: #236097;
}
.services--intro__heading strong {
  font-weight: 900;
}
.services--intro__heading::after {
  content: "";
  position: absolute;
  left: 50%;
  transform: translateX(-50%);
  bottom: -1.5rem;
  left: 50%;
  width: 29rem;
  height: 0.5rem;
  background-color: #236097;
}
.services--intro p {
  padding-top: 4rem;
  font-weight: 600;
}
.services--box {
  width: 30.7rem;
  height: 45rem;
  overflow: hidden;
  border-radius: 0.6rem;
  box-shadow: 0.25em 0.25em 0.75em rgba(0, 0, 0, 0.25), 0.125em 0.125em 0.25em rgba(0, 0, 0, 0.15);
}
.services--box__container {
  display: flex;
  height: 40rem;
  width: 100%;
  padding: 6rem 0 0 11rem;
}
.services--box:not(:last-child) {
  margin-right: 8rem;
}
.services--image {
  transition: all 300ms;
  height: 100%;
  width: 100%;
  object-fit: cover;
}
.services--image:hover {
  filter: brightness(65%);
}
.services--foot {
  position: relative;
  width: 100%;
  height: 9rem;
  font-size: 3rem;
  padding-top: 2.5rem;
  padding-left: 11%;
  transform: translateY(-100%);
  transition: all 300ms;
  background-color: #0080ff;
  color: #fff;
}
.services--foot:hover {
  background-color: #236097;
}
.services--foot::after {
  position: absolute;
  top: 50%;
  transform: translateY(-50%);
  content: "";
  clip-path: polygon(0 100%, 0 0, 60% 50%);
  height: 3rem;
  width: 3rem;
  right: 2rem;
  background-color: #fff;
}

.action {
  height: max-content;
  width: 100%;
  background-color: whitesmoke;
}
.action--heading {
  color: #236097;
  font-size: 4.5rem;
  margin-bottom: 4rem;
}
.action--heading__container {
  position: relative;
  height: 38rem;
  margin: 0 0 5rem 11rem;
}
.action--heading__png {
  position: absolute;
  left: 0;
  top: 7rem;
  width: 35rem;
}
.action--heading__sub {
  position: absolute;
  top: 21.5rem;
  left: 10%;
  color: #236097;
}
.action--heading__text {
  position: absolute;
  width: 89rem;
  font-size: 3rem;
  top: 29rem;
}
.action--heading__image {
  position: absolute;
  width: 32rem;
  height: 32rem;
  top: 5.5rem;
  right: 5.5rem;
  object-fit: cover;
  border-radius: 1rem;
  box-shadow: 0.25em 0.25em 0.75em rgba(0, 0, 0, 0.25), 0.125em 0.125em 0.25em rgba(0, 0, 0, 0.15);
}
.action--heading strong {
  font-weight: 900;
}

.book {
  position: relative;
  background-image: linear-gradient(90deg, #d1dce6 0%, #d1dce6 45.5%, transparent 45%), linear-gradient(90deg, rgba(209, 220, 230, 0.8) 100%, rgba(209, 220, 230, 0.3) 54.5%, transparent 55%), url("/5782584a9162f167b7ba0a17dc4c613f.jpg");
  background-size: cover;
  border-radius: 3px;
}
.book::before {
  content: "";
  position: absolute;
  background-color: #d1dce6;
  height: 84rem;
  bottom: 0;
  width: 50vw;
  left: -50vw;
}
.book__form {
  width: 50%;
  padding: 6rem 11rem 6rem 11rem;
}

.details {
  position: absolute;
  top: 50%;
  transform: translateY(-50%);
  top: 49%;
  right: 0;
  color: #236097;
  height: 71.5rem;
  width: 77.5rem;
}
.details--heading {
  font-weight: 600;
  color: inherit;
  line-height: 5.5rem;
  margin-bottom: 5rem;
}
.details--heading strong {
  font-weight: 900;
}
.details a {
  display: flex;
  align-items: center;
  gap: 2.5rem;
  font-size: 2.5rem;
  font-weight: 600;
  color: inherit;
}
.details a:not(:last-child) {
  margin-bottom: 4.5rem;
}
.details a img {
  width: 6rem;
  filter: invert(28%) sepia(72%) saturate(936%) hue-rotate(176deg) brightness(94%) contrast(84%);
}

.map {
  position: relative;
  width: 100%;
  height: max-content;
  margin-top: 10rem;
}
.map--heading {
  width: 100%;
  height: 20rem;
  background-color: #236097;
}
.map--heading::before {
  position: absolute;
  content: "";
  height: 20rem;
  width: 50vw;
  transform: translateX(-99%);
  background-color: inherit;
}
.map--heading div {
  padding-top: 3.5rem;
  text-align: center;
}
.map--heading div img {
  filter: invert(100%) sepia(91%) saturate(38%) hue-rotate(254deg) brightness(110%) contrast(110%);
  height: 5rem;
  margin-bottom: 2rem;
}
.map--heading::after {
  position: absolute;
  content: "";
  height: 20rem;
  width: 50vw;
  top: 0;
  right: 0;
  transform: translateX(99%);
  background-color: inherit;
}
.map--container {
  margin-top: 10rem;
  border-top: 1rem solid #236097;
  border-bottom: 1rem solid #236097;
  width: 152rem;
  height: 94rem;
  margin-bottom: 15rem;
}

.footer {
  position: absolute;
  left: 50%;
  transform: translateX(-50%);
  height: 56rem;
  width: 100vw;
  background-color: #236097;
}
.footer--grid {
  display: grid;
  width: 152rem;
  margin: 0 auto;
  padding-top: 4rem;
  grid-template-rows: 44.5rem 0.5rem 7rem;
  grid-template-columns: repeat(3, 1fr);
}
.footer--left {
  grid-column: 1/2;
  grid-row: 1/2;
}
.footer--left__logo {
  width: 25rem;
  margin-top: 1rem;
  filter: invert(100%) sepia(91%) saturate(38%) hue-rotate(254deg) brightness(110%) contrast(110%);
}
.footer--left__logo:hover {
  transition: all 150ms;
  filter: invert(32%) sepia(40%) saturate(4926%) hue-rotate(198deg) brightness(105%) contrast(103%);
}
.footer--left__links {
  display: flex;
  gap: 3rem;
  flex-direction: column;
  margin: 4.5rem 0 0 0.5rem;
}
.footer--left__links a {
  display: flex;
  align-items: center;
  gap: 2rem;
}
.footer--left__links a img {
  width: 10rem;
  filter: invert(100%) sepia(91%) saturate(38%) hue-rotate(254deg) brightness(110%) contrast(110%);
  border-radius: 2.5rem;
}
.footer--left__links a img:hover {
  transition: all 150ms;
  filter: invert(32%) sepia(40%) saturate(4926%) hue-rotate(198deg) brightness(105%) contrast(103%);
}
.footer--left__links a p {
  color: #fff;
  font-size: 3.5rem;
}
.footer--left__links a p:hover {
  transition: all 150ms;
  color: #0080ff;
}
.footer--middle {
  grid-column: 2/3;
  grid-row: 1/2;
}
.footer--middle h3 {
  margin-top: 0.6rem;
  font-size: 4rem;
  color: white;
  font-weight: 600;
}
.footer--middle h3 strong {
  font-weight: 900;
}
.footer--middle__content {
  display: flex;
  flex-direction: column;
}
.footer--middle__content a {
  margin-top: 2rem;
  color: #fff;
  font-size: 3rem;
}
.footer--middle__content a:hover {
  transition: all 150ms;
  color: #0080ff;
}
.footer--right {
  grid-column: 3/4;
  grid-row: 1/2;
  background-color: #fff;
}
.footer--right__container {
  display: flex;
  flex-direction: column;
  place-items: center;
}
.footer--right__container img {
  margin-top: 2.5rem;
  width: 35rem;
}
.footer--divider {
  grid-column: 1/4;
  grid-row: 2/3;
  background-color: #fff;
}
.footer--bottom {
  grid-column: 1/4;
  grid-row: 3/4;
  justify-self: end;
  align-self: center;
}
.footer--bottom p {
  color: #fff;
  font-size: 2.5rem;
  padding-right: 2rem;
}

